htmlform.script:
jQuery(document).ready(function(){
    const dateToDMY = function(date) {
        var d = date.getDate();
        var m = date.getMonth() + 1; //Month from 0 to 11
        var y = date.getFullYear();
        return '' + (d > 9 ? d : '0' + d) + '.' + (m > 9 ? m : '0' + m ) + '.' + y;
    };

    const addAccordionToVitalsSection = function() {
        jQuery('#capture-vitals-accordion').click(() => {
            if (jQuery('#vitals:visible').length) {
                // hide vitals section
                jQuery('#vitals').slideUp();
                jQuery('#capture-vitals-accordion > i').removeClass('icon-chevron-down');
                jQuery('#capture-vitals-accordion > i').addClass('icon-chevron-right');
            } else {
                // show vitals section
                jQuery('#vitals').slideDown();
                jQuery('#capture-vitals-accordion > i').removeClass('icon-chevron-right');
                jQuery('#capture-vitals-accordion > i').addClass('icon-chevron-down');
            }
        });
        // set vitals hidden by default
        jQuery('#vitals').slideUp();
    }

    const convertPlannedDateToDMYFormat = function() {
        var plannedDate = jQuery('#plannedDate').text();
        jQuery('#plannedDate').text(dateToDMY(new Date(plannedDate)));
    }

    const convertActualVisitDateDatePickerToDMYFormat = function() {
        $('#encounterDate')[0].innerHTML = $('#encounterDate')[0].innerHTML.replace('(mm/dd/yyyy)','');
        jQuery('#w1-display').removeClass('hasDatepicker');
        var selectedDate = jQuery('#w1').attr('value');
        // we need to instantiate new date picker
        setupDatePicker('dd.mm.yy', '110,20','en-GB', '#w1-display', '#w1', selectedDate);
    }

    const replaceDiagnosisSectionLabels = function() {
        $('[for="diagnosis-search"]').after('<i>Choose primary diagnosis first, then secondary diagnosis</i>');
        $('[for="diagnosis-search"]').text('Add diagnoses - presumed or confirmed (optional)');
    }

    const calculateBmi = function(wt, ht) {
        var bmi = null;
        if (wt && ht) {
            bmi = wt / ((ht/100) * (ht/100));
        }
        return bmi;
    }

    const updateBmi = function() {
        const wt = htmlForm.getValueIfLegal('weight.value');
        const ht = htmlForm.getValueIfLegal('height.value');

        const bmi = calculateBmi(wt, ht);

        if (bmi == null || isNaN(bmi)) {
            jq('#calculated-bmi-wrapper').hide();
        } else {
            jq('#no-calculated-bmi').hide();
            jq('#calculated-bmi-wrapper').show();
            jq('#calculated-bmi').html(bmi.toFixed(1));
            jq('#hidden-calculated-bmi').val(bmi.toFixed(1));
        }
    };

    const attachBmiHandler = function() {
        jq(function() {
            jq('#calculated-bmi-wrapper').hide();
            getField('weight.value').change(updateBmi);
            getField('height.value').change(updateBmi);
            updateBmi();
        });

        jq(function() {
            jq('#bp_systolic input[type=text]').attr('min', '50');
        });

        jq(function() {
            jq('#bp_diastolic input[type=text]').attr('min', '30');
        });
    }

    // executions
    addAccordionToVitalsSection();
    convertPlannedDateToDMYFormat();
    convertActualVisitDateDatePickerToDMYFormat();
    replaceDiagnosisSectionLabels();
    attachBmiHandler();
});

ifMode.script:
// handle displaying the bmi when in VIEW mode
jq(function() {

// we have to iterate through in case there are multiple vitals forms
// displayed on a single page

jq('htmlform').each(function(index, form) {
jq(form).find('#calculated-bmi-continue').hide();
jq(form).find('#no-calculated-bmi').hide();

var wt = jq(form).find('#weight').find('.value').text();
var ht = jq(form).find('#height').find('.value').text();

var bmi = calculateBmi(wt, ht);

if (bmi != null && !isNaN(bmi)) {
jq(form).find('#calculated-bmi-wrapper').show();
jq(form).find('#calculated-bmi').html(bmi.toFixed(1));
jq(form).find('#hidden-calculated-bmi').val(bmi.toFixed(1));
}
});

});
